-- ----------------------------
-- Table structure for NewsInformation
-- ----------------------------
DROP TABLE IF EXISTS `NewsInformation`;
CREATE TABLE `NewsInformation` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `NewsTitle` varchar(200) NOT NULL,
  `NewsShortContent` varchar(400) NOT NULL,
  `CreateTime` bigint(20) NOT NULL COMMENT '时间戳',
  `IsInUse` int(11) NOT NULL COMMENT '0：无效  1：有效',
  `Creater` varchar(30) NOT NULL COMMENT '创建人账户',
  `NewsTotalContent` varchar(2000) NOT NULL,
  `HotImg` varchar(100) DEFAULT NULL COMMENT '存放热点图片在服务器的路径',
  `HotContImg` varchar(100) DEFAULT NULL COMMENT '存放内容图片在服务器路径',
  `Author` varchar(10) NOT NULL,
  `CompanyID` int(11) NOT NULL,
  `NewsType` varchar(20) NOT NULL COMMENT '军事、政治、娱乐。。。。',
  `LastModify` varchar(20) NOT NULL,
  `NewsStatus` int(11) NOT NULL COMMENT '待审核 1，审批完成2',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=76 DEFAULT CHARSET=utf8 COMMENT='新闻详细信息表';

