INSERT INTO `NewsInformation` VALUES ('4', '马龙宣布退赛 日本选手不战而胜', '北京时间6月23日，中国乒乓球公开赛正在成都进行，据国际乒联报道，在晚上进行的男单比赛中，马龙宣布退赛， 日本选手大岛祐哉不战而胜。之前，马龙在微博上发声力挺刘国梁。', '1498229405272', '1', 'root', '北京时间6月23日，中国乒乓球公开赛正在成都进行，据国际乒联报道，在晚上进行的男单比赛中，马龙宣布退赛， 日本选手大岛祐哉不战而胜。之前，马龙在微博上发声力挺刘国梁。北京时间6月23日，中国乒乓球公开赛正在成都进行，据国际乒联报道，在晚上进行的男单比赛中，马龙宣布退赛， 日本选手大岛祐哉不战而胜。之前，马龙在微博上发声力挺刘国梁。北京时间6月23日，中国乒乓球公开赛正在成都进行，据国际乒联报道，在晚上进行的男单比赛中，马龙宣布退赛， 日本选手大岛祐哉不战而胜。之前，马龙在微博上发声力挺刘国梁。', null, null, '啦啦啦', '1', '体育', 'Jay调我想要', '2');
